import React from "react";
import styled from "styled-components";
import TV from "../components/TV";
import TVMobile from "../components/TVMobile"
import { device } from "../device";
import slotsplace from "../images/slotsplace.png";
import { Desktop, MobileAndTablet } from "react-responsive-simple";



const Columns = styled.div`
  display: flex;
  width: 100%;
  border-bottom: 10px solid #C4C4C4;
  padding-bottom: 5vw;
`;

const TVColumn = styled.div`
  width: 60%;

  @media ${device.mobile} {
    width:0;
  }
`;

const ArticleColumn = styled.div`
  width: 40%;
  padding-left: 5vw;
  padding-right: 2vw;

  @media ${device.mobile} {
    width:100%;
    padding-right:0%;
  }
`;

const Title = styled.div`
  text-align: center;
  font-size: 5rem;
  background-color: white;
  width: fit-content;
  font-weight: 900;
  margin-left: 4vw;
  padding: 1vw;
  border: 1rem solid #E9C898;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);

  :hover{
    cursor: pointer;
  }
`;

const MobileTitle = styled.div`
  text-align: center;
  font-size: 2rem;
  background-color: white;
  width: fit-content;
  font-weight: 900;
  padding: 1vw;
  border: 0.5rem solid #E9C898;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  margin-left: 29%;

  :hover{
    cursor: pointer;
  }
`;

const Slots = styled.div`
    background-image: url(${slotsplace});
    background-size: 80%;
    margin-left: 5vw;
    margin-top: 3vw;
    background-repeat: no-repeat;
    overflow: hidden;
    width: 100%;
    height: 40vw;
    position: relative;
    top: 0;
    /* background-image: url(${(props) => props.imageCall}) */
`;

const MobileSlots = styled.div`
    background-image: url(${slotsplace});
    background-size: 80%;
    margin-top: 3vw;
    background-repeat: no-repeat;
    overflow: hidden;
    width: 100%;
    height: 120vw;
    margin-left: 20%;
    top: 0;
    /* background-image: url(${(props) => props.imageCall}) */
`;

const Sections = ({ handleClick }) => {
  return (
    <>
      <Desktop>
        <Columns>
          <TVColumn>
            <TV/>
          </TVColumn>
          <ArticleColumn>
              <Title onClick={handleClick}>Click Here!</Title>
              <Slots trigger={handleClick} imageCall={Math.random() * 10}></Slots>
          </ArticleColumn>
        </Columns>
      </Desktop>
      <MobileAndTablet>
        <TVMobile/> 
        <MobileTitle onClick={handleClick}>Click Here!</MobileTitle>
        <MobileSlots trigger={handleClick} imageCall={Math.random() * 10}></MobileSlots>
      </MobileAndTablet>
    </>
  );
};

export default Sections;
